##function do_pandoc {echo "Covering HTML to Markdown via Pandoc"set -xprintf "### Wiki File: ${FILE} | $(date) | DReagan auto-convert via 'pandoc'\n\n" > ${P_MD}pandoc --ignore-args -r html -w markdown < ${FILE} | sed '/<div class="site-info">/,$d'  >> ${P_MD}set -}###FILE="$1"if [[ ! -e ${FILE} ]] ; then printf "\n\tError - Need File name: '${FILE}' not present...\n\n" ; exit 1 ; fiP_MD=${FILE}.p.markdowndo_pandoc